        [Test]
        public void MapOldRealDocumentToNewIsOk()
        {
            var mapper = SwissKnife.Database.ImportDataService.CreateMappingFromOldToNew();
            var jsonSerializerOptions = new JsonSerializerOptions
            {
                Encoder = JavaScriptEncoder.UnsafeRelaxedJsonEscaping
            };
            
            var jsonTexts = GetSamples();
            foreach (var item in jsonTexts)
            {
                var assessment = JsonSerializer.Deserialize<FA3Legacy>(item);

                var newassesment = new Assessment { Doc = JsonSerializer.Serialize(mapper.Map<FA4>(assessment), jsonSerializerOptions) };
            }
        }

